import React, { useState } from 'react';

const AddData = () => {
  const [Company , setCompany] = useState()
  const [Address , setAddress] = useState()
  const [Material , setMaterial] = useState()
  const [QRT, setQRT] = useState()
  const [Reta, setReta] = useState()
  
  const handleAdd=()=>{
    const ProductAdd = async () => {
      if (!name || !price || !category || !company) {
        setError(true);
        return;
      }
    
      const user = JSON.parse(localStorage.getItem("user") || "{}");
      const userId = user._id;
    
      if (!userId) {
        console.error("User not found or invalid in localStorage");
        return;
      }
    
      try {
        const response = await fetch("http://localhost:5000/Add-Product", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name, price, category, company, userId }),
        });
    
        const result = await response.json();
        console.log("Product added:", result);
    
        // Reset fields
        setName("");
        setPrice("");
        setCategory("");
        setCompany("");
        setError(false);
      } catch (error) {
        console.error("Failed to add product:", error);
      }
    };
    

  }
  

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Add Company Data</h2>

      <form onSubmit={handleAdd} className="row g-3 mb-4">
        <div className="col-md-4">
        <input
        type="text"
        className="form-control"
        placeholder="Company Name"
        value={Company}
        onChange={(e) => setCompany(e.target.value)}
      />
        </div>
        <div className="col-md-4">
          <input 
           type="text" 
           className="form-control"
           name="address" 
           placeholder="Address" 
           value={Address}
           onChange={(e)=>setAddress(e.target.value)}
           />
        </div>
        <div className="col-md-4">
          <input 
          type="text" 
          className="form-control" 
          name="material" 
          placeholder="Material"
          value={Material}
          onChange={(e)=>setMaterial(e.target.value)}/>
        </div>
        <div className="col-md-2">
          <input 
          type="text"
           className="form-control" 
           name="qrt" placeholder="QRT" 
           value={QRT} onChange={(e)=>setQRT(e.target.value)}/>
        </div>
        <div className="col-md-2">
          <input 
          type="text" 
          className="form-control" 
          name="reta" 
          placeholder="Reta" 
          value={Reta} 
          onChange={(e)=>setReta(e.target.value)} required />
        </div>
        <div className="col-md-2">
          <button type="submit" className="btn btn-success w-100">Add</button>
        </div>
      </form>
    </div>
  );
};

export default AddData;
