import React, { useState } from 'react';


const App = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);

  return (
    <div>
      {/* Navbar with buttons */}
      <nav className="navbar navbar-light bg-light px-4">
        <a className="navbar-brand" href="#">OMS</a>
        <div className="d-flex gap-3">
          <button
            type="button"
            className="btn btn-primary"
            onClick={() => {
              setShowLogin(true);
              setShowSignUp(false);
            }}
          >
            Login
          </button>

          <button
            type="button"
            className="btn btn-outline-primary"
            onClick={() => {
              setShowSignUp(true);
              setShowLogin(false);
            }}
          >
            Sign Up
          </button>
        </div>
      </nav>

      {/* Conditional rendering of forms */}
      <div className="mt-4">
        {showLogin && <Login />}
        {showSignUp && <SignUp />}
      </div>
    </div>
  );
};

export default App;
