import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Login from './Login';
import SignUp from './SignUp';

const Nav = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);
  const navigate = useNavigate();
  const auth = localStorage.getItem('admin');
  const admin = auth && auth !== 'undefined' ? JSON.parse(auth) : null;

  useEffect(() => {
    if (!admin) {
      navigate('/signup');
    }
  }, [admin, navigate]);

  const logout = () => {
    localStorage.removeItem('admin');
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <h1 className="navbar-brand">OMS WebApp</h1>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link active ms-5" to="/home">Home</Link>
              </li>
              <li className="nav-item">
                <Link className='nav-link active ms-5' to="/data">Data List</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link active ms-5" to="/adddata">Add Data</Link>
              </li>
            </ul>

            <ul className="navbar-nav ms-auto">
              {admin ? (
                <>
                  <li className="nav-item">
                    <span className="nav-link text-white">Welcome, {admin.name}</span>
                  </li>
                  <li className="nav-item">
                    <button onClick={logout} className="btn btn-danger btn-sm ms-2">Log Out</button>
                  </li>
                </>
              ) : (
                <>
                  <li className="nav-item">
                    <Link className="btn btn-primary btn-sm me-2" to="/login">Login</Link>
                  </li>
                  <li className="nav-item">
                    <Link className="btn btn-primary btn-sm" to="/signup">Signup</Link>
                  </li>
                </>
              )}
            </ul>
          </div>
        </div>
      </nav>

      {/* Show forms conditionally */}
      <div className="container mt-4">
        {showLogin && <Login />}
        {showSignUp && <SignUp />}
      </div>
    </>
  );
};

export default Nav;
