import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Login from './Login';
import SignUp from './SignUp';

const Nav = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);
  const auth = localStorage.getItem('admin');
  const admin = auth && auth !== 'undefined' ? JSON.parse(auth) : null;
  const navigate = useNavigate();

  useEffect(() => {
    if (!admin) {
      navigate('/signup');
    }
  }, [admin, navigate]);

  const logout = () => {
    localStorage.clear();
    console.log("Logout");
    navigate('/signup'); // ✅ redirect on logout
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <h1 className="navbar-brand">OMS WebApp</h1>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link active ms-5" to="/home">Home</Link>
              </li>
              <li className="nav-item">
                <Link className='nav-link active ms-5' to="/data">Data List</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link active ms-5" to="/adddata">Add Data</Link>
              </li>
              <li>
                <button onClick={logout} className="btn btn-danger ms-5">
                  Log Out  Log Out {admin?.name ? `(${admin.name})` : ''}
                </button>
              </li>
            </ul>
          </div>

          {!admin && (
            <div className="d-flex gap-3">
              <button
                type="button"
                className="btn btn-primary"
                onClick={() => {
                  setShowLogin(true);
                  setShowSignUp(false);
                }}
              >
                Login
              </button>

              <button
                type="button"
                className="btn btn-primary"
                onClick={() => {
                  setShowSignUp(true);
                  setShowLogin(false);
                }}
              >
                SignUp
              </button>
            </div>
          )}
        </div>
      </nav>

      {/* Show forms conditionally */}
      <div className="container mt-4">
        {showLogin && <Login />}
        {showSignUp && <SignUp />}
      </div>
    </>
  );
};

export default Nav;
