import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Nav = () => {
  const navigate = useNavigate();
  const auth = localStorage.getItem('D');
  const user = auth ? JSON.parse(auth) : null;

  const logout = () => {
    localStorage.clear();
    navigate('/signup');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-4">
      <Link className="navbar-brand fw-bold" to="/">OMS WebApp</Link>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav me-auto">
          <li className="nav-item">
            <Link className="nav-link" to="/">Home</Link>
          </li>
          {user && (
            <>
              <li className="nav-item">
                <Link className="nav-link" to="/datalist">Data List</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/adddata">Add Data</Link>
              </li>
            </>
          )}
        </ul>
        <ul className="navbar-nav ms-auto">
          {user ? (
            <>
              <li className="nav-item d-flex align-items-center">
                <span className="text-white me-2">Welcome, {user.name}</span>
              </li>
              <li className="nav-item">
                <button className="btn btn-danger btn-sm" onClick={logout}>
                  Log Out
                </button>
              </li>
            </>
          ) : (
            <>
              <li className="nav-item">
                <Link className="btn btn-outline-light btn-sm me-2" to="/login">Login</Link>
              </li>
              <li className="nav-item">
                <Link className="btn btn-outline-light btn-sm" to="/signup">Signup</Link>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Nav;
